import React, {useEffect, useState} from "react";
import { Link} from 'react-router-dom';

//Styles
import style from '../styles/managerPage/table.css';

//Images
import description from "../images/1x/descriptionIcon.png";
import description2x from "../images/2x/descriptionIcon@2x.png";
import description3x from "../images/3x/descriptionIcon@3x.png";







const options = [
    {value: 0, label: 'PENDIENTE'},
    {value: 1, label: 'ACTIVO'},
    {value: 2, label: 'CERRADO'}
]

function Table() {

    const url = 'http://localhost:5000/action/list';

    const [records, setrecords] = useState();
    const fetchApi = async () => {
        const response = await fetch(url);
        const data = await response.json();
        setrecords(data);
        console.log(data['data']['recordsets'][0]);
    };

    useEffect(() => {
        fetchApi();
    }, []);  

    
    
    // fetch('http://localhost:5000/action/list')
    //     // //acceder al recordset de la respuesta
    //     // .then(response => response.json())
    //     // //acceder a los datos del recordset
    //     // .then(data => {
    //     //     console.log(data.json());
    //     //     //acceder a los datos de cada registro
    //     //     data.forEach(element => {
    //     //         console.log(element);
    //     //     });
    //     // })
    //     // .catch(error => console.log(error));



    //     .then(response => response.json())
    //     .then(data => console.log(data['data']['recordsets'][0]))
    //     .catch(error => console.log(error));

    return (

        
        <div className="table-container">

            <table>

                <thead>

                    <tr>

                        <th>ID</th>
                        <th>R.Date</th>
                        <th>State</th>
                        <th>A.Date</th>
                        <th>Type</th>
                        <th>Responsible</th>
                        <th>Settled</th>
                        <th>Name</th>
                        <th>Last Name</th>
                        <th>E-mail</th>
                        <th>Contact Num</th>
                        <th>Company</th>
                        <th>Description</th>

                    </tr>

                </thead>

                <tbody>

                    {
                        records && records['data']['recordsets'][0].map((record) => (
                            <tr>
                                <td>{record.ID}</td>

                            
                                <td>
                                    <select>

                                        {options.map((option) => (
                                            <option key={option.value} value={option.value}>
                                                {option.label}
                                            </option>
                                        ))}

                                    </select>
                                </td>
                                



                                <td><Link to="/description"><img className = "descriptionIcon" srcSet={`${description} 1x, ${description2x} 2x, ${description3x} 3x`} src={description} alt="customerManagerText" /></Link></td>

                            </tr>
                    ))}
                        

                </tbody>

            </table>

        </div>
    );
}

export {Table};
