import React, {useEffect, useState}  from "react";
import { ReactDOM } from "react-dom";
import { Route, Routes } from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';

//Styles
import style from '../styles/managerPage/table.css';

//Component

import { Header } from "../managerComponents/Header";
import { Background } from "../managerComponents/Background";
import { Table } from "../managerComponents/Table";
import { Description } from "../managerComponents/Description"; 

function ManagerPage(){
    //API-GET

    const recordsData = [];

    const url = 'http://localhost:5000/action/list';

    const [records, setrecords] = useState();
    const fetchApi = async () => {
        const response = await fetch(url);
        const data = await response.json();
        setrecords(data);
    };

    useEffect(() => {
        fetchApi();
    }, []);  

    records && records['data']['recordsets'][0].map((record) => {
        recordsData.push(record);
    });

    const states = [];
    const aDate = [];
    const requestsTypes = [];

    recordsData.map((record) => {
        if (record.IDEstado === 1) {
            states.push('Pendiente');
        } else if (record.IDEstado === 2) {
            states.push('Asignado');
        } else if (record.IDEstado === 3) {
            states.push('Resuelto');
        }

        if (record.Fecha === null) {
            aDate.push('No Asignar');
        } else {
            aDate.push(record.Fecha);
        }

        if (record.IDSolicitud === 1){
            requestsTypes.push('PQRS');
        } else if (record.IDSolicitud === 2){
            requestsTypes.push('Novedades');
        } else if (record.IDSolicitud === 3){
            requestsTypes.push('Reclamos');
        }
    });

    return(
        <React.Fragment>


                <Header />

                <Background />

                <Routes>

                    <Route path="/" element={<Table data={recordsData} states={states} aDate={aDate}/>} />

                    <Route path="/description" element={<Description />} />

                </Routes>


        </React.Fragment>
    );
}


export {ManagerPage};
