import React, {Fragment, useState} from "react";

//style

import style from '../styles/customerPage/form.css';

const options = [
    {value: 1, label: 'PQRS'},
    {value: 2, label: 'New'},
    {value: 3, label: 'Development'}
]   


  
function Form(props){

    const [form, setForm] = useState({
        NombreSolicitante: "",
        ApellidoSolicitante: "",
        CorreoSolicitante: "",
        TelefonoSolicitante: '',
        NombreEmpresa: "",
        IdTipoSolicitud: '',
        Descripcion: ""
    });

    const handleInputChange = (event) => {
        console.log(event.target.value);
        setForm({
            ...form,
            [event.target.name]: event.target.value
        })
    }

    const post = () => {

    }

    return(
        <div className='form-container' >
        
            <div className="header" id="form">

                <div className="circle"></div>
                <div className="circle"></div>
                <div className="circle"></div>

            </div>

            <form method="POST"  autoComplete="on" onSubmit={post}>

                <div className="inputs-container">

                    <div className="form-left">

                        <label className="form-label" >Name *</label>

                        <input className="form-input" type="name" name='NombreSolicitante' required minLength={3} onChange={handleInputChange}/>

                        <label className="form-label">Email *</label>

                        <input className="form-input" type="email"  name='CorreoSolicitante' required/>

                        <label className="form-label">Company Name</label>

                        <input className="form-input" type="text" name='NombreEmpresa' />

                    </div>

                    <div className="form-right">

                        <label className="form-label">Last Name *</label>

                        <input className="form-input" type="text" name='ApellidoSolicitante' required minLength={3}/>

                        <label className="form-label">Contact Number *</label>

                        <input className="form-input" type="tel" name='TelefonoSolicitante' required minLength={10} pattern="[0-9]+"/>

                        <label className="form-label">Request Type</label>

                        <select name='IdTipoSolicitud' onChange={(event, requestTypeValue) => {
                            requestTypeValue = event.target.value;
                            console.log(requestTypeValue)
                            }}>

                            {options.map((option) => (
                                <option key={option.value} value={option.value}>
                                    {option.label}
                                </option>
                            ))}
                            
                        </select>

                    </div>
                </div>

                

                <div className="form-bottom">
                
                    <label className="form-label">Describe Your Need *</label>
                
                    <textarea className="form-textarea" name="describeYourNeed" id="describeYourNeed" cols="30" rows="10" required></textarea>

                </div>

                <div className="form-button">

                    <button className="form-button-send" onClick={(event)=> console.log('hola')} >Send</button>

                </div>

            </form>

        </div>
    );
}

export {Form};
