import React, {useEffect, useState} from "react";
import { Link} from 'react-router-dom';

//Styles
import style from '../styles/managerPage/table.css';

//Images
import description from "../images/1x/descriptionIcon.png";
import description2x from "../images/2x/descriptionIcon@2x.png";
import description3x from "../images/3x/descriptionIcon@3x.png";







const options = [
    {value: 0, label: 'PENDIENTE'},
    {value: 1, label: 'ASIGNADO'},
]

function Table() {

    const url = 'http://localhost:5000/action/list';

    const [records, setrecords] = useState();
    const fetchApi = async () => {
        const response = await fetch(url);
        const data = await response.json();
        setrecords(data);
        console.log(data['data']['recordsets'][0]);
    };

    useEffect(() => {
        fetchApi();
    }, []);  

    const recordsData = [];

    records && records['data']['recordsets'][0].map((record) => {
        recordsData.push(record);
    });

    console.log(recordsData[0].IDEstado);

    recordsData[0].IDEstado = 8;

    // console.log(recordsData[0][5]);




    return (

        
        <div className="table-container">

            <table>

                <thead>

                    <tr>

                        <th>ID</th>
                        <th>R.Date</th>
                        <th>State</th>
                        <th>A.Date</th>
                        <th>Type</th>
                        <th>Responsible</th>
                        <th>Settled</th>
                        <th>Name</th>
                        <th>Last Name</th>
                        <th>E-mail</th>
                        <th>Contact Num</th>
                        <th>Company</th>
                        <th>Description</th>

                    </tr>

                </thead>                

                <tbody>

                    {
                        records && records['data']['recordsets'][0].map((record) => (


                            <tr>
                                <td>{record.IDSolicitud}</td>
                                <td>{record.FechaSolicitud}</td>
                                <td>{record.IDEstado}</td>
                                <td>{record.FechaRespuesta}</td>
                                <td>{record.IdTipoSolicitud}</td>
                                <td>{record.IDResponsable}</td>
                                <td>{record.Radicado}</td>
                                <td>{record.NombreSolicitante}</td>
                                <td>{record.ApellidoSolicitante}</td>
                                <td>{record.CorreoSolicitante}</td> 
                                <td>{record.TelefonoSolicitante}</td>
                                <td>{record.NombreEmpresa}</td>
                                
                                <td><Link to="/description"><img className = "descriptionIcon" srcSet={`${description} 1x, ${description2x} 2x, ${description3x} 3x`} src={description} alt="customerManagerText" /></Link></td>

                            </tr>
                    ))}

                        

                </tbody>

            </table>

        </div>
    );
}

export {Table};
