import React, {useEffect, useState}  from "react";
import { ReactDOM } from "react-dom";
import { Route, Routes } from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';

//Styles
import style from '../styles/managerPage/table.css';

//Component

import { Header } from "../managerComponents/Header";
import { Background } from "../managerComponents/Background";
import { Table } from "../managerComponents/Table";
import { Description } from "../managerComponents/Description"; 

function ManagerPage(){
    //API-GET-RERQUEST

    const recordsData = [];

    const url = 'http://localhost:5000/action/list';

    const [records, setrecords] = useState();
    const fetchApi = async () => {
        const response = await fetch(url);
        const data = await response.json();
        setrecords(data);
    };

    useEffect(() => {
        fetchApi();
    }, []);  

    records && records['data']['recordsets'][0].map((record) => {
        recordsData.push(record);
    });





    const states = [];
    const aDate = [];
    const requestsTypes = [];
    const asignaments = [];

    recordsData.map((record) => {
        if (record.IDEstado === 1) {
            record.IDEstado = ('Pending');
        } else if (record.IDEstado === 2) {
            record.IDEstado = ('Assigned');
        } else if (record.IDEstado === 3) {
            record.IDEstado = ('Resuelto');
        }

        if (record.FechaRespuesta === null) {
            record.FechaRespuesta = ('Unassigned');
        } else {
            record.FechaRespuesta = (record.FechaRespuesta);
        }

        if (record.IdTipoSolicitud === 1){
            record.IdTipoSolicitud = ('PQRS');
        } else if (record.IdTipoSolicitud === 2){
            record.IdTipoSolicitud = ('News');
        } else if (record.IdTipoSolicitud === 3){
            record.IdTipoSolicitud = ('Developments');
        }

        if (record.IDResponsable === 1){
            record.IDResponsable = ('xxx');
        } else if (record.IDResponsable === 2){
            record.IDResponsable = ('xxx');
        } else if (record.IDResponsable === 3){
            record.IDResponsable = ('xxx');
        } else if (record.IDResponsable === null) {
            record.IDResponsable  = ('Unassigned');
        }
    });

    return(
        <React.Fragment>


                <Header />

                <Background />

                <Routes>

                    <Route path="/" element={<Table data={recordsData} states={states} aDate={aDate} requestsTypes={requestsTypes} asignaments={asignaments}/>} />

                    <Route path="/description" element={<Description />} />

                </Routes>


        </React.Fragment>
    );
}


export {ManagerPage};
