import React from "react";

//style

import style from '../styles/customerPage/form.css';

const options = [
    {value: 1, label: 'PQRS'},
    {value: 2, label: 'New'},
    {value: 3, label: 'Development'}
]   


  
function Form(props){

    const selectValue = '';
    const nameValue = '';
    const emailValue = '';
    const companyNameValue = '';
    const lsatNameValue = '';
    

    return(
        <div className='form-container' >
        
            <div className="header" id="form">

                <div className="circle"></div>
                <div className="circle"></div>
                <div className="circle"></div>

            </div>

            <form method="POST"  autoComplete="on">

                <div className="inputs-container">

                    <div className="form-left">

                        <label className="form-label">Name *</label>

                        <input className="form-input" type="name" required minLength={3}/>

                        <label className="form-label">Email *</label>

                        <input className="form-input" type="email" required/>

                        <label className="form-label">Company Name</label>

                        <input className="form-input" type="text" />

                    </div>

                    <div className="form-right">

                        <label className="form-label">Last Name *</label>

                        <input className="form-input" type="text" required minLength={3}/>

                        <label className="form-label">Contact Number *</label>

                        <input className="form-input" type="tel" required minLength={10} pattern="[0-9]+"/>

                        <label className="form-label">Request Type</label>

                        <select onChange={(event, selectValue) => {
                            selectValue = event.target.value;
                            console.log(selectValue)
                            }}>

                            {options.map((option) => (
                                <option key={option.value} value={option.value}>
                                    {option.label}
                                </option>
                            ))}
                            
                        </select>

                    </div>
                </div>

                

                <div className="form-bottom">
                
                    <label className="form-label">Describe Your Need *</label>
                
                    <textarea className="form-textarea" name="describeYourNeed" id="describeYourNeed" cols="30" rows="10" required></textarea>

                </div>

                <div className="form-button">

                    <button className="form-button-send">Send</button>

                </div>

            </form>

        </div>
    );
}

export {Form};
